mapper.select:
select * from(
select
rownum rnum, n1.* from(
select * from
tb_notice
order by ${orderby} desc
)n1
) where rnum between #{page.start} and #{page.end}

mapper.insert:
insert into tb_file values(
S_TB_FILE_IDX.NEXTVAL
,
S_NOTICE.currval
, #{originFileName}
, #{renameFileName}
,
#{savePath}
)
